package generator

const _baseTemplate = `
// Code generated by protoc-gen-yarpc-go
// source: {{.File.Name}}
// DO NOT EDIT!

package {{.File.Package.GoPackage}}

{{if .File.Services -}}
  import (
    {{range .Imports -}}{{printf "%q\n" .}}{{end -}}
  )
{{end -}}

{{template "client" . -}}
{{template "server" . -}}
`
